import UIKit

extension String {

    public func size(with font: UIFont, width: CGFloat = .greatestFiniteMagnitude, height: CGFloat = .greatestFiniteMagnitude) -> CGSize {
        let nsString = self as NSString
        let rect = nsString.boundingRect(with: CGSize(width: width, height: height),
                                         options: [.usesLineFragmentOrigin, .usesFontLeading],
                                         attributes: [NSAttributedString.Key.font: font],
                                         context: nil)

        return CGSize(width: ceil(rect.width), height: ceil(rect.height))
    }

    public func height(with font: UIFont, width: CGFloat) -> CGFloat {
        return size(with: font, width: width).height
    }

    public func width(with font: UIFont, height: CGFloat) -> CGFloat {
        return size(with: font, height: height).width
    }



